{
    int year = 2023;

    if (year % 4 == 0) {
        // first nested if-else statement
        if (year % 100 == 0) {
            // second nested if-else statement
            if (year % 400 == 0) {
                cout &lt;&lt; year &lt;&lt; " is a leap year.";
